package kr.co.cylearn.common.base;import java.io.Serializable;import org.apache.commons.lang.builder.EqualsBuilder;import org.apache.commons.lang.builder.HashCodeBuilder;import org.apache.commons.lang.builder.ToStringBuilder;import org.apache.commons.lang.builder.ToStringStyle;@SuppressWarnings("serial")public class BaseObject implements Serializable {	/**	 * @see java.lang.Object#toString()	 * {@inheritDoc}	 */	public String toString() {		return ToStringBuilder.reflectionToString(this, ToStringStyle.MULTI_LINE_STYLE);	}	/**	 * @see java.lang.Object#equals(java.lang.Object)	 * {@inheritDoc}	 */	public boolean equals(Object obj) {		return EqualsBuilder.reflectionEquals(this, obj);	}	/**	 * @see java.lang.Object#hashCode()	 * {@inheritDoc}	 */	public int hashCode() {		return HashCodeBuilder.reflectionHashCode(this);	}}